import { Component, ViewChild } from '@angular/core'
import { EmpTableService } from '../services/emp-table.service'
import { PrimeNGConfig } from 'primeng/api'
import { FormControl, FormGroup, NgForm, Validators } from '@angular/forms'
import { HttpClient } from '@angular/common/http'

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class DashboardComponent {
  empList: any
  value3: any
  displayBasic: any
  // Name: any
  // showDialog: any
  visible: any
  first: number = 0

  rows: number = 10

  constructor(
    private post: EmpTableService,
    private primengConfig: PrimeNGConfig,
    private http: HttpClient,
  ) {}

  // ---------------Employee list From Json.DB------------------
  ngOnInit() {
    this.setTableData()
  }
  setTableData() {
    this.post.employee().subscribe((response) => {
      this.empList = response
      console.log(this.empList)
    })
    this.primengConfig.ripple = true
  }

  // -----------------DialogBox Validations--------------------------

  dialogForm = new FormGroup({
    Name: new FormControl('', [Validators.required]),
    mobile: new FormControl('', [
      Validators.required,
      Validators.pattern('^((\\+91-?)|0)?[0-9]{10}$'),
    ]),
    Address: new FormControl('', [Validators.required]),
    Age: new FormControl('', [Validators.required]),
  })
  get Name() {
    return this.dialogForm.get('Name')
  }
  get mobile() {
    return this.dialogForm.get('mobile')
  }
  get Address() {
    return this.dialogForm.get('Address')
  }
  get Age() {
    return this, this.dialogForm.get('Age')
  }

  // ----------------post Method-----------------------
  displayResponsive(userData: any) {
    console.log(userData)
    let valueOfForm
    valueOfForm = userData.value
    this.http
      .post('http://localhost:3000/Users', valueOfForm)
      .subscribe((res) => {
        console.log(res)
        this.setTableData()
        this.visible = true
      })
    // this.displayBasic = false
  }
  showBasicDialog() {
    this.visible = true
  }
  onPageChange(event: any) {
    this.first = event.first
    this.rows = event.rows
  }

  // onDelete(deleteRec: any) {
  //   console.log(deleteRec)
  // }
}
