import { Component } from '@angular/core'
import { EmpTableService } from '../services/emp-table.service'
import { PrimeNGConfig } from 'primeng/api'
import { FormControl, FormGroup, Validators } from '@angular/forms'

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class DashboardComponent {
  empList: any
  value3: any
  displayBasic: any
  Name: any
  showDialog: any
  constructor(
    private post: EmpTableService,
    private primengConfig: PrimeNGConfig,
  ) {}

  // ---------------Employee list From Json.DB------------------
  ngOnInit() {
    this.post.employee().subscribe((response) => {
      this.empList = response
      console.log(this.empList)
    })
    this.primengConfig.ripple = true
  }
  showBasicDialog() {
    this.displayBasic = true
  }
  displayResponsive() {
    this.displayBasic = false
  }
  // -----------------DialogBox Validations--------------------------

  dialogForm = new FormGroup({
    dName: new FormControl('', [Validators.required]),
    mNumber: new FormControl('', [
      Validators.required,
      Validators.pattern('^((\\+91-?)|0)?[0-9]{10}$'),
    ]),
    address: new FormControl('', [Validators.required]),
    age: new FormControl('', [Validators.required]),
  })
  get dName() {
    return this.dialogForm.get('dName')
  }
  get mNumber() {
    return this.dialogForm.get('mNumber')
  }
  get address() {
    return this.dialogForm.get('address')
  }
  get age() {
    return this, this.dialogForm.get('age')
  }
  onSubmit() {
    console.log(this.dialogForm.value)
  }
}
